    private void btnNextMainWinActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNextMainWinActionPerformed

        String grid = comboGridSize.getSelectedItem().toString();



        new Board(grid.charAt(0) - 48, Integer.parseInt(comboNoOfBalls.getSelectedItem().toString()));

        this.dispose();
    }//GEN-LAST:event_btnNextMainWinActionPerformed

