    private void comboGridSizeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comboGridSizeActionPerformed
        int selectedIndex = comboGridSize.getSelectedIndex();
        switch (selectedIndex) {

            case 0:
                comboNoOfBalls.removeAllItems();
                for (int i = 1; i <= 3; i++) {
                    comboNoOfBalls.addItem(i);
                }
                break;

            case 1:
                comboNoOfBalls.removeAllItems();
                for (int i = 1; i <= 4; i++) {
                    comboNoOfBalls.addItem(i);
                }
                break;

            case 2:
                comboNoOfBalls.removeAllItems();
                for (int i = 1; i <= 5; i++) {
                    comboNoOfBalls.addItem(i);
                }
                break;
        }
    }//GEN-LAST:event_comboGridSizeActionPerformed

